/**
 * 打印样式
 */
function printStyle(gridLineColor) {
    return "\n<style>\n  html, body {\n    margin: 0;\n    padding: 8px;\n  }\n  @page {\n    size: auto;\n    margin: 0mm;\n  }\n  table {\n    border-collapse: collapse;\n    width: 100%;\n  }\n  table, th, td {\n    border: 1px solid ".concat(gridLineColor, ";\n  }\n</style>\n");
}

export { printStyle };
