import { presetIcons } from '../../io/excel/preset/presetIcons.js';

function drawIconSet(canvas, icon, x, y, width, height) {
    var iconURL = presetIcons[icon];
    if (!iconURL) {
        console.warn('未知的图标', icon);
        return;
    }
    // 避免挡住网格线
    canvas.drawImageWithCache(iconURL, x + 1, y + 1, height - 2, height - 2);
}

export { drawIconSet };
