import { __assign } from 'tslib';
import { MAX_ROW } from '../Consts.js';

/**
 * 点击到列表头
 * @param workbook
 * @param hitTestResult
 * @returns
 */
function mousedownColHeader(workbook, hitTestResult) {
    var activeCell = {
        startRow: 0,
        startCol: hitTestResult.startCol,
        endRow: 0,
        endCol: hitTestResult.endCol
    };
    return {
        user: '',
        region: hitTestResult.region,
        selectType: hitTestResult.type,
        activeCell: activeCell,
        sheetIndex: workbook.getActiveSheet().getIndex(),
        cellRanges: [
            __assign(__assign({}, activeCell), { endRow: MAX_ROW })
        ]
    };
}

export { mousedownColHeader };
