import { __assign } from 'tslib';
import { MAX_COL } from '../Consts.js';

/**
 * 点击到行表头
 * @param workbook
 * @param hitTestResult
 * @returns
 */
function mousedownRowHeader(workbook, hitTestResult) {
    var activeCell = {
        startRow: hitTestResult.startRow,
        startCol: 0,
        endRow: hitTestResult.endRow,
        endCol: 0
    };
    return {
        user: '',
        region: hitTestResult.region,
        selectType: hitTestResult.type,
        activeCell: activeCell,
        sheetIndex: workbook.getActiveSheet().getIndex(),
        cellRanges: [
            __assign(__assign({}, activeCell), { endCol: MAX_COL })
        ]
    };
}

export { mousedownRowHeader };
