import { __assign } from 'tslib';
import { MAX_ROW, MAX_COL } from '../Consts.js';

/**
 * 全选范围
 */
function selectAll(sheetIndex) {
    var activeCell = {
        startRow: 0,
        startCol: 0,
        endRow: 0,
        endCol: 0
    };
    return {
        user: '',
        region: 'normal',
        selectType: 'corner',
        activeCell: activeCell,
        sheetIndex: sheetIndex,
        cellRanges: [
            __assign(__assign({}, activeCell), { endRow: MAX_ROW, endCol: MAX_COL })
        ]
    };
}

export { selectAll };
