/**
 * Drawing 渲染基类
 */
var BaseDrawingRender = /** @class */ (function () {
    function BaseDrawingRender(container, displayRect, gid, className) {
        var picContainer = document.createElement('div');
        picContainer.className = className;
        picContainer.dataset.gid = gid;
        this.drawingContainer = picContainer;
        this.updatePosition(displayRect);
        container.appendChild(picContainer);
    }
    BaseDrawingRender.prototype.hide = function () {
        this.drawingContainer.style.display = 'none';
    };
    BaseDrawingRender.prototype.show = function () {
        this.drawingContainer.style.display = 'block';
    };
    BaseDrawingRender.prototype.updatePosition = function (displayRect) {
        var x = displayRect.x, y = displayRect.y, width = displayRect.width, height = displayRect.height;
        this.drawingContainer.style.left = "".concat(x, "px");
        this.drawingContainer.style.top = "".concat(y, "px");
        this.drawingContainer.style.width = "".concat(width, "px");
        this.drawingContainer.style.height = "".concat(height, "px");
    };
    return BaseDrawingRender;
}());

export { BaseDrawingRender };
