import { copySelection } from '../selection/copySelection.js';

/**
 * 复制
 */
function handleCopy(e, workbook) {
    if (e.metaKey || e.ctrlKey) {
        // 全选
        if (e.key === 'a') {
            console.log('SELECT ALL');
        }
        // 复制
        if (e.key === 'c') {
            copySelection(workbook);
        }
    }
}

export { handleCopy };
