import { handleCopy } from './handleCopy.js';
import { handleSelectAll } from './handleSelectAll.js';
import { handleSheetCanvasKeydown } from './handleSheetArrowKey.js';

/**
 * 处理键盘事件
 */
function handleKeydown(e, workbook) {
    handleSelectAll(e, workbook);
    handleCopy(e, workbook);
    handleSheetCanvasKeydown(e, workbook);
}

export { handleKeydown };
