import { selectAll } from '../dnd/selectAll.js';

/**
 * 复制
 */
function handleSelectAll(e, workbook) {
    if (e.metaKey || e.ctrlKey) {
        // 全选
        if (e.key === 'a') {
            var sheetIndex = workbook.getActiveSheet().getIndex();
            workbook.uiEvent.emit('CHANGE_SELECTION', selectAll(sheetIndex));
            e.preventDefault();
        }
    }
}

export { handleSelectAll };
