/**
 * 二分查找到到 hitTest 的位置
 * @param sizes 位置信息
 * @param position 要查找的位置
 * @returns
 */
function binarySearchSize(sizes, position) {
    var found = -1;
    var start = 0;
    var end = sizes.length - 1;
    while (start <= end) {
        var mid = Math.floor((start + end) / 2);
        var size = sizes[mid];
        if (size.offset <= position && size.offset + size.size > position) {
            return mid;
        }
        else if (size.offset < position) {
            start = mid + 1;
        }
        else {
            end = mid - 1;
        }
    }
    return found;
}

export { binarySearchSize };
