/**
 * 二分查找，目前只支持数字数组
 * @returns 找到就返回索引，找不到就返回 -1
 */
function binarySearch(nums, target) {
    var left = 0;
    var right = nums.length - 1;
    while (left <= right) {
        var mid = Math.floor((left + right) / 2);
        if (nums[mid] === target) {
            return mid;
        }
        if (target < nums[mid]) {
            right = mid - 1;
        }
        else {
            left = mid + 1;
        }
    }
    return -1;
}

export { binarySearch };
