import { createElement } from '../../util/dom.js';

/**
 * 渲染书签
 */
function renderBookmarkStart(word, bookmarkStart) {
    var name = bookmarkStart.name;
    if (name) {
        var bookmark = createElement('a');
        bookmark.name = name;
        bookmark.id = name;
        return bookmark;
    }
    return null;
}

export { renderBookmarkStart };
